mapper.select:
SELECT 
FROM ious iou
WHERE iou.iou_code = #{iouCode}

SELECT , ,
FROM ious iou
LEFT JOIN users u on u.user_id = iou.borrower_id
LEFT JOIN users ul on ul.user_id = iou.lender_id
WHERE 1=1

	AND u.phone_number = #{phoneNumber}


	AND u.userName = #{userName}


	AND iou.iou_code = #{iouCode}



	AND iou.lender_id = #{lenderId}
	AND iou.is_lender_deleted = 0

		AND (
			u.phone_number = #{searchText} 
			OR iou.borrower_id = #{searchText} 
			OR u.user_name = #{searchText} 
		) 



	AND iou.borrower_id = #{borrowerId}
	AND iou.is_deleted = 0

		AND (
			ul.phone_number = #{searchText} 
			OR iou.lender_id = #{searchText} 
			OR ul.user_name = #{searchText} 
		) 




	AND iou.borrowing_date >= #{borrowingStart}


	AND iou.borrowing_date <= #{borrowingEnd}


	AND iou.repayment_date >= #{repaymentStart}


	AND iou.repayment_date <= #{repaymentEnd}


	AND iou.borrowing_amount >= #{amountMin}


	AND iou.borrowing_amount <= #{amountMax}


	AND 


			(iou.repayment_date >= date(now()) AND iou.status in (1,5,6,7,8,10,11,12,13))


			iou.status in (9,14)


			(iou.repayment_date < date(now()) AND iou.status in (1,5,6,7,8,10,11,12,13))




		AND iou.status IN 

			#{status}




			AND iou.iou_code < #{lastIouCode}


			AND iou.iou_code > #{lastIouCode}




		ORDER BY iou.iou_code DESC


		ORDER BY iou.iou_code ASC


		ORDER BY iou.iou_code DESC


LIMIT #{pageSize}

SELECT 
FROM ious iou
LEFT JOIN (
	SELECT DISTINCT r.iou_code FROM iou_alert_records r 
	WHERE r.message_type = #{messageType} AND DATE(r.creation_date) = DATE(#{sendDate})
)  t ON iou.iou_code = t.iou_code
WHERE t.iou_code IS NULL

 		AND iou.repayment_date  >= #{startDate}


 		AND iou.repayment_date  <  #{endDate}


		AND iou.status IN 

			#{status}


LIMIT #{pageSize}

SELECT , FROM ious iou LEFT JOIN users u on u.user_id = iou.borrower_id WHERE iou.is_deleted= 0 AND iou.is_lender_deleted= 0 AND iou.status = 0
AND iou.iou_code < #{lastIouCode}
AND iou.iou_code > #{lastIouCode}
ORDER BY iou.iou_code DESC
ORDER BY iou.iou_code ASC
ORDER BY iou.iou_code DESC
LIMIT #{pageSize}

SELECT , , iiu.status AS intendStatus FROM iou_intentional_users iiu INNER JOIN ious iou ON iou.iou_code = iiu.iou_code LEFT JOIN users u ON u.user_id = iou.borrower_id WHERE iiu.user_id = #{lenderId}
AND iiu.status IN
#{status}
ORDER BY iou.last_update_status_date DESC

SELECT count(1) FROM ious iou WHERE iou.borrower_id = #{borrowerId} AND iou.is_deleted = 0
AND iou.status IN
#{status}

		SELECT 
		FROM ious iou
		WHERE 1=1 AND iou.borrower_id = #{borrowerId} AND iou.is_deleted= 0

			AND iou.status IN

				#{status}



			AND iou.status NOT IN

				#{status}




           		AND IFNULL(extension_date, repayment_date) >= #{currentDate}
                ORDER BY IFNULL(extension_date, repayment_date)  ASC


           		AND IFNULL(extension_date, repayment_date) < #{currentDate}
                ORDER BY IFNULL(extension_date, repayment_date)  DESC


		Limit #{size}

SELECT count(1) as cnt,  
	sum(borrowing_amount * annual_rate * DATEDIFF(IF(iou.status=9 or iou.status=14, repayment_date, now()), borrowing_date)/365/100 ) as interest,
	sum(borrowing_amount) as amount 
FROM ious iou
WHERE 1=1

	AND iou.status IN

		#{status}



	AND  iou.repayment_date >= #{start}


	AND  iou.repayment_date < #{end}


	AND  iou.borrower_id = #{borrowerId}


	AND  iou.lender_id = #{lenderId}

SELECT EXISTS (
	SELECT 1 FROM iou_status_control
	WHERE pre_status = #{preStatus} 
		AND new_status = #{newStatus}
		AND borrower_ctrl = #{borrowerCtrl}
		AND lender_ctrl = #{lenderCtrl}
)

SELECT EXISTS (
	SELECT 1 FROM iou_status_control
	WHERE pre_status = #{preStatus}
		AND new_status = #{newStatus}
		AND need_trade_password = 1
)
