mapper.select:
SELECT , FROM mgt_loan_platform_statistic m LEFT JOIN mgt_fin_files f ON m.platform_id= f.platform_id AND m.statistic_date=DATE(f.file_date) AND f.is_deleted=0 WHERE m.statistic_date >= #{startDate} AND m.platform_id = #{platformId}
AND m.statistic_date <= #{endDate}
ORDER BY statistic_date

SELECT FROM mgt_loan_platform_statistic m WHERE m.platform_id = #{platformId}
AND m.statistic_date = DATE(#{statisticDate})
AND m.statistic_date < DATE(#{statisticDate}) ORDER BY m.statistic_date DESC LIMIT 1

SELECT t1.statistic_date AS statisticDate,t1.platform_id AS platformId,
WHERE 1=1

	AND t1.platform_id in

		#{platformId}


AND t1.statistic_date >= #{startDate}

	AND t1.statistic_date <= #{endDate}

GROUP BY t1.statistic_date

mapper.sql:
SUM(cnt*price)/SUM(m.cnt) AS price, SUM(cnt*price)/SUM(CASE WHEN m.uv_cnt !=0 THEN m.uv_cnt ELSE t1.uvCnt END) AS uvPrice, SUM(CASE WHEN m.uv_cnt !=0 THEN m.uv_cnt ELSE t1.uvCnt END) As uvCnt, SUM(CASE WHEN m.click_cnt !=0 THEN m.click_cnt ELSE t1.click_cnt END) As clickCnt, SUM(m.cnt) AS cnt, SUM(m.cnt*1.0)/SUM(CASE WHEN m.uv_cnt !=0 THEN m.uv_cnt ELSE t1.uvCnt END) AS uvRates, SUM(cnt*price) AS money from ( SELECT l.cookie_id as platform_id, DATE(l.creation_date) as statistic_date, COUNT(DISTINCT l.user_id) AS uvCnt,COUNT(l.user_id) AS clickCnt From mgt_user_event_logs l where DATE(l.creation_date)>= #{startDate}
AND DATE(l.creation_date) <= #{endDate}
AND l.event_name in ('daichao','jbb2','bnh','xhyp') group by l.platform_id , DATE(l.creation_date) ) t1 left join mgt_loan_platform_statistic m on m.statistic_date = t1.statistic_date and m.platform_id = t1.platform_id

mapper.select:
SELECT t1.statistic_date AS statisticDate,t1.platform_id AS platformId,p.`name` AS name,
LEFT JOIN mgt_loan_platforms p ON t1.platform_id=p.platform_id
WHERE 1=1

	AND t1.platform_id in

		#{platformId}


AND t1.statistic_date >= #{startDate}

	AND t1.statistic_date <= #{endDate}

GROUP BY t1.platform_id

SELECT

FROM
mgt_loan_platform_statistic l
WHERE
l.statistic_date >= #{startDate}
AND
l.statistic_date < #{endDate}

	AND l.platform_id = #{platformId}

SELECT p.group_name AS groupName , , , , acc.nickname as nickname FROM mgt_loan_platform_statistic m LEFT JOIN mgt_loan_platforms p ON m.platform_id = p.platform_id LEFT JOIN mgt_fin_files f ON m.platform_id = f.platform_id AND m.statistic_date = DATE(f.file_date) AND f.is_deleted = 0 LEFT JOIN mgt_accounts acc ON acc.account_id = p.sales_id WHERE 1=1
AND statistic_date = DATE(#{statisticDate})
AND m.statistic_date >= #{startDate}
AND m.statistic_date <= #{endDate}
AND m.platform_id = #{platformId}
AND p.group_name = #{groupName}
AND p.type = #{type}
AND p.sales_id=#{salesId}
ORDER BY statistic_date, m.balance asc

SELECT m.statistic_date AS statisticDate , , , , acc.nickname AS nickname FROM mgt_loan_platform_statistic m LEFT JOIN mgt_loan_platforms p ON m.platform_id = p.platform_id LEFT JOIN mgt_fin_files f ON m.platform_id = f.platform_id AND m.statistic_date = DATE(f.file_date) AND f.is_deleted = 0 LEFT JOIN mgt_accounts acc ON acc.account_id = p.sales_id WHERE 1=1
AND m.statistic_date >= #{startDate}
AND m.statistic_date <= #{endDate}
AND m.platform_id = #{platformId}
AND p.group_name = #{groupName}
AND p.type = #{type}
AND p.sales_id=#{salesId}
ORDER BY statistic_date DESC,p.group_name

SELECT lps.statistic_date AS statisticDate, lps.platform_id AS platformId, lps.amount AS amount, lps.balance AS balance, lps.expense AS expense, (case WHEN p.price_mode=2 THEN p.uv_price ELSE p.cpa_price END) AS price, (case WHEN p.price_mode=2 THEN lps.uv_cnt ELSE 0 END) AS uvCnt
,(case WHEN p.price_mode=1 THEN lps.uv_cnt/2 ELSE 0 END) AS cnt
FROM mgt_loan_platform_statistic lps LEFT JOIN mgt_loan_platforms p ON lps.platform_id=p.platform_id WHERE lps.statistic_date >= #{startDate} AND lps.statistic_date < #{endDate}

mapper.update:
update mgt_loan_platform_statistic p
set
	p.price=#{price},
	p.puv_cnt=#{uvCnt},
	p.cnt=#{cnt},
	p.amount=#{amount},
	p.expense=#{expense},
	p.balance=#{balance}
where p.platform_id = #{platformId} 
   AND p.statistic_date=#{statisticDate}
