mapper.select:
SELECT i.*
,d.*,m.*
FROM mgt_tele_marketing_init i
left join mgt_tele_marketing_detail d on d.id = i.id left join mgt_tele_marketing m on d.batch_id = m.batch_id
where i.init_account_id=#{accountId}
and i.op_commet_flag = #{isMarked}

mapper.update:
UPDATE mgt_tele_marketing m 
SET m.status = #{status},
	m.valid_cnt = #{validCnt},
	m.creation_date = #{creationDate}
WHERE m.batch_id = #{batchId}
