SELECT count(1)
FROM mgt_user_event_logs l
where 1=1

	AND l.user_id = #{userId}


	AND l.source_id = #{sourceId}


	AND l.cookie_id = #{cookieId}


	AND l.event_name = #{eventName}


	AND l.event_action = #{eventAction}


	AND l.event_label = #{eventLabel}


	AND l.event_value = #{eventValue}


	AND l.creation_date >= #{start}


	AND l.creation_date < #{end}

SELECT count(1)
FROM mgt_user_event_logs l
where 1=1

	AND l.user_id = #{userId}


	AND l.source_id = #{sourceId}


	AND l.cookie_id = #{cookieId}


	AND l.event_name = #{eventName}


	AND l.event_action = #{eventAction}


	AND l.event_label = #{eventLabel}


	AND l.event_value = #{eventValue}


	AND l.event_value2 = #{eventValue2}


	AND l.creation_date >= #{start}


	AND l.creation_date < #{end}

SELECT log.cookie_id AS platformId, lp.`name` AS platformName, lp.group_name AS groupName, acc.nickname AS nickname, count( log.user_id ) AS clickCnt, count( DISTINCT log.user_id ) AS puCnt, lp.estimated_uv_cnt as estimatedUvCnt FROM mgt_loan_platforms lp LEFT JOIN mgt_user_event_logs log ON log.cookie_id = cast(lp.platform_id as char) LEFT JOIN mgt_accounts acc ON acc.account_id = lp.sales_id WHERE log.event_name in ('daichao','jbb2','bnh','xhyp') AND lp.frozen = 0
AND log.creation_date >= #{startDate}
AND log.creation_date < #{endDate}
AND lp.sales_id = #{salesId}
AND lp.group_name = #{groupName}
AND lp.platform_id = #{platformId}
GROUP BY lp.platform_id ORDER BY lp.group_name,puCnt DESC
